package chapter1a;

public class FootToMeter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double FOOTTOMETER = .305;
		final double FEET = 49;
		System.out.println(FEET + " feet is " + FOOTTOMETER * FEET + " meters.");
		
	}

}
